import React from 'react';

const Note = ({ id, title, content, createdAt, onDelete }) => {
  const handleDelete = () => {
    onDelete(id);
  };

  return (
    <div className="note-card-container fade-in-up">
      <div className="card h-100 shadow-sm border-0 note-card">
        <div className="card-header bg-transparent border-0 d-flex justify-content-between align-items-start p-3">
          <div className="note-meta">
            <small className="text-muted d-flex align-items-center">
              <i className="fas fa-calendar-alt me-1"></i>
              {createdAt}
            </small>
          </div>
          <button
            onClick={handleDelete}
            className="btn btn-outline-danger btn-sm rounded-circle delete-btn"
            title="Delete note"
          >
            <i className="fas fa-trash-alt"></i>
          </button>
        </div>
        
        <div className="card-body p-3 pt-0">
          <h5 className="card-title fw-bold text-dark mb-3 note-title">
            {title}
          </h5>
          <p className="card-text text-muted note-content">
            {content}
          </p>
        </div>
        
        <div className="card-footer bg-transparent border-0 p-3 pt-0">
          <div className="d-flex justify-content-between align-items-center">
            <small className="text-muted">
              <i className="fas fa-sticky-note me-1"></i>
              Note
            </small>
            <div className="note-actions">
              <button className="btn btn-outline-primary btn-sm rounded-pill me-2">
                <i className="fas fa-edit me-1"></i>
                Edit
              </button>
              <button className="btn btn-outline-info btn-sm rounded-pill">
                <i className="fas fa-share me-1"></i>
                Share
              </button>
            </div>
          </div>
        </div>
      </div>
      
      <style jsx>{`
        .note-card-container {
          height: 100%;
        }
        
        .note-card {
          background: rgba(255, 255, 255, 0.95);
          backdrop-filter: blur(10px);
          border-radius: var(--border-radius);
          transition: var(--transition);
          position: relative;
          overflow: hidden;
          border-left: 4px solid var(--primary-color) !important;
        }
        
        .note-card:hover {
          transform: translateY(-5px);
          box-shadow: var(--shadow-hover) !important;
        }
        
        .note-card::before {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
          right: 0;
          height: 2px;
          background: linear-gradient(90deg, var(--primary-color), var(--accent-color));
        }
        
        .note-title {
          color: var(--dark-color);
          font-size: 1.1rem;
          line-height: 1.4;
          word-wrap: break-word;
        }
        
        .note-content {
          font-size: 0.95rem;
          line-height: 1.6;
          word-wrap: break-word;
          max-height: 120px;
          overflow: hidden;
          text-overflow: ellipsis;
          display: -webkit-box;
          -webkit-line-clamp: 5;
          -webkit-box-orient: vertical;
        }
        
        .delete-btn {
          width: 32px;
          height: 32px;
          display: flex;
          align-items: center;
          justify-content: center;
          transition: var(--transition);
          border: 1px solid #dc3545;
        }
        
        .delete-btn:hover {
          background-color: #dc3545;
          color: white;
          transform: scale(1.1);
        }
        
        .note-actions button {
          font-size: 0.8rem;
          padding: 0.25rem 0.75rem;
          transition: var(--transition);
        }
        
        .note-actions button:hover {
          transform: translateY(-1px);
        }
        
        .note-meta {
          opacity: 0.7;
        }
        
        @media (max-width: 768px) {
          .note-actions {
            display: flex;
            flex-direction: column;
            gap: 0.5rem;
          }
          
          .note-actions button {
            font-size: 0.75rem;
            width: 100%;
          }
          
          .card-footer .d-flex {
            flex-direction: column;
            align-items: flex-start;
            gap: 1rem;
          }
        }
        
        @media (max-width: 576px) {
          .note-actions {
            width: 100%;
          }
          
          .note-actions button {
            flex: 1;
          }
          
          .note-actions {
            flex-direction: row;
          }
        }
      `}</style>
    </div>
  );
};

export default Note;
