import React, { useState } from 'react';

const CreateArea = ({ onAdd }) => {
  const [note, setNote] = useState({
    title: '',
    content: ''
  });
  const [isExpanded, setIsExpanded] = useState(false);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setNote(prevNote => ({
      ...prevNote,
      [name]: value
    }));
  };

  const submitNote = (event) => {
    event.preventDefault();
    onAdd(note);
    setNote({
      title: '',
      content: ''
    });
    setIsExpanded(false);
  };

  const expand = () => {
    setIsExpanded(true);
  };

  return (
    <div className="create-area-container">
      <div className="card shadow-lg border-0 create-area-card">
        <div className="card-body p-4">
          <form onSubmit={submitNote}>
            {isExpanded && (
              <div className="mb-3 fade-in-up">
                <input
                  name="title"
                  onChange={handleChange}
                  value={note.title}
                  placeholder="Enter note title..."
                  className="form-control form-control-lg border-0 bg-light title-input"
                  autoComplete="off"
                />
              </div>
            )}
            
            <div className="mb-3">
              <textarea
                name="content"
                onChange={handleChange}
                value={note.content}
                placeholder={isExpanded ? "Write your note content..." : "Take a note..."}
                rows={isExpanded ? 4 : 1}
                onClick={expand}
                className="form-control border-0 bg-light content-textarea"
                style={{ resize: 'none', transition: 'all 0.3s ease' }}
              />
            </div>
            
            {isExpanded && (
              <div className="d-flex justify-content-between align-items-center fade-in-up">
                <div className="d-flex gap-2">
                  <button
                    type="button"
                    className="btn btn-outline-secondary btn-sm rounded-pill"
                    onClick={() => {
                      setIsExpanded(false);
                      setNote({ title: '', content: '' });
                    }}
                  >
                    <i className="fas fa-times me-1"></i>
                    Cancel
                  </button>
                </div>
                
                <button
                  type="submit"
                  className="btn btn-primary btn-lg rounded-pill px-4 add-btn"
                >
                  <i className="fas fa-plus me-2"></i>
                  Add Note
                </button>
              </div>
            )}
          </form>
        </div>
      </div>
      
      <style jsx>{`
        .create-area-container {
          margin-bottom: 2rem;
        }
        
        .create-area-card {
          background: rgba(255, 255, 255, 0.95);
          backdrop-filter: blur(10px);
          border-radius: var(--border-radius);
          transition: var(--transition);
          position: relative;
          overflow: hidden;
        }
        
        .create-area-card::before {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
          right: 0;
          height: 4px;
          background: linear-gradient(90deg, var(--primary-color), var(--accent-color));
        }
        
        .create-area-card:hover {
          transform: translateY(-2px);
          box-shadow: var(--shadow-hover) !important;
        }
        
        .title-input,
        .content-textarea {
          border: none !important;
          box-shadow: none !important;
          font-family: 'Poppins', sans-serif;
          font-size: 1rem;
        }
        
        .title-input {
          font-weight: 600;
          font-size: 1.1rem;
        }
        
        .title-input:focus,
        .content-textarea:focus {
          background-color: #f8f9fa !important;
          outline: none;
        }
        
        .add-btn {
          background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
          border: none;
          font-weight: 600;
          transition: var(--transition);
          box-shadow: var(--shadow);
        }
        
        .add-btn:hover {
          transform: translateY(-2px);
          box-shadow: var(--shadow-hover);
          background: linear-gradient(135deg, var(--secondary-color), var(--primary-color));
        }
        
        .add-btn:active {
          transform: translateY(0);
        }
        
        @media (max-width: 768px) {
          .create-area-card {
            margin: 0 10px;
          }
          
          .d-flex.justify-content-between {
            flex-direction: column;
            gap: 1rem;
          }
          
          .add-btn {
            width: 100%;
          }
        }
      `}</style>
    </div>
  );
};

export default CreateArea;
