import React, { useState } from 'react';
import Header from './components/Header';
import CreateArea from './components/CreateArea';
import Note from './components/Note';
import Footer from './components/Footer';
import Alert from './components/Alert';
import './App.css';

const App = () => {
  const [notes, setNotes] = useState([]);
  const [alert, setAlert] = useState({ show: false, message: '', type: '' });

  const addNote = (newNote) => {
    if (!newNote.title.trim() || !newNote.content.trim()) {
      showAlert('Please fill in both title and content fields!', 'danger');
      return;
    }
    
    setNotes(prevNotes => [
      ...prevNotes,
      {
        id: Date.now(),
        title: newNote.title,
        content: newNote.content,
        createdAt: new Date().toLocaleDateString()
      }
    ]);
    
    showAlert('Note added successfully!', 'success');
  };

  const deleteNote = (id) => {
    setNotes(prevNotes => prevNotes.filter(note => note.id !== id));
    showAlert('Note deleted successfully!', 'info');
  };

  const showAlert = (message, type) => {
    setAlert({ show: true, message, type });
    setTimeout(() => {
      setAlert({ show: false, message: '', type: '' });
    }, 3000);
  };

  return (
    <div className="App">
      <Header />
      <Alert alert={alert} />
      <div className="container my-4">
        <div className="row justify-content-center">
          <div className="col-lg-8 col-md-10 col-12">
            <CreateArea onAdd={addNote} />
            <div className="notes-container mt-4">
              {notes.length === 0 ? (
                <div className="text-center py-5">
                  <i className="fas fa-sticky-note fa-3x text-muted mb-3"></i>
                  <h4 className="text-muted">No notes yet</h4>
                  <p className="text-muted">Create your first note above!</p>
                </div>
              ) : (
                <div className="row">
                  {notes.map(note => (
                    <div key={note.id} className="col-lg-6 col-md-6 col-12 mb-3">
                      <Note
                        id={note.id}
                        title={note.title}
                        content={note.content}
                        createdAt={note.createdAt}
                        onDelete={deleteNote}
                      />
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default App;
